        "FROM (delivery INNER JOIN facilities ON\n\n" .
        "delivery.FacilityID = facilities.FacilityID) INNER JOIN newborn ON\n\n" .
        "newborn.DeliveryID = delivery.DeliveryID {SQL_Where} {SQL_OrderBy}";
